import React, {useEffect, useState} from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from "react-infinite-scroll-component";

const News = (props) => {
    const [articles, setArticles] = useState([])
    const [loading, setLoading] = useState(true)
    const [page, setPage] = useState(1)
    const [totalResults, setTotalResults] = useState(0)
    // document.title = `${capitalizeFirstLetter(props.category)} - MayankNews`;

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };


  const updateNews = async() => {
    props.setProgress(10);
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=06116f28526f48bcae830fb9eeff9cb2&page=${state.page}&pageSize=${props.pageSize}`  
    setLoading(true);
    let data = await fetch(url);
    props.setProgress(30);
    let parsedData = await data.json();
    props.setProgress(70);
    // setState({ articles: parsedData.articles, totalResults: parsedData.totalResults, loading: false });
    props.setProgress(100);
    setArticles(parsedData.articles)
    setTotalResults(parsedData.totalResults)
    setLoading(false)
  }

  useEffect(() => {
    updateNews();
  },[])


  const handlePrevClick = async (props) => {
    setPage(page-1)  
    updateNews(props);
  };

  const handleNextClick = async (props) => {
    setPage(page+1)
    updateNews(props);
  };

  const fetchMoreData = async () => {
    setPage(page+1)
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=06116f28526f48bcae830fb9eeff9cb2&page=${state.page}&pageSize=${props.pageSize}`  
    let data = await fetch(url);
    let parsedData = await data.json();
    setArticles(articles.concat(parsedData.articles))
    setTotalResults( parsedData.totalResults)
  };

    return (
      <div className="container my-3">
        <h1 className="text-center" style={{ margin: '35px' }}>MayankNews - Top {capitalizeFirstLetter(props.category)} Headlines </h1>
        {state.loading && <Spinner />}
        <InfiniteScroll
          dataLength={state.articles.length}
          next={fetchMoreData}
          hasMore={state.articles.length !== state.totalResults}
          loader={<Spinner />}
        >
          <div className="container">
            <div className="row">
              {state.articles.map((element) => (
                <div className="col-md-4" key={element.url}>
                  <Newsitem
                    title={element.title ? element.title : ""}
                    description={element.description ? element.description.slice() : ""}
                    imageurl={element.urlToImage}
                    newsurl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              ))}
            </div>
          </div>
        </InfiniteScroll>
        <div className="container d-flex justify-content-between">
          <button className="btn btn-dark" onClick={handlePrevClick}>&larr; Previous</button>
          <button disabled={state.page + 1 > Math.ceil(state.totalResults / props.pageSize)} className="btn btn-dark" onClick={handleNextClick}>Next &rarr;</button>
        </div>
      </div>

    );


}


News.defaultProps = {
  country: 'in',
  pageSize: 8,
  category: 'general',
};

News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,

};

export default News;
