import React, {useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import News from './components/News';
import LoadingBar from 'react-top-loading-bar'

const App = () => {
  const pageSize = 15;
  const state = {
   progress: 0
  };

  const[progress, setProgress] = useState(0)

    return (
      <div>
        <Router>
          <Navbar />
          <LoadingBar color="#f11946" progress={this.state.progress} />
          <Routes>
            <Route exact
              path="/"
              element={<News setProgress={this.setProgress} key="general" pageSize={pageSize} country="in" category="general" />}
            />
            <Route
              exact
              path="/business"
              element={<News setProgress={this.setProgress} key="business" pageSize={pageSize} country="in" category="business" />}
            />
            <Route
              exact
              path="/entertainment"
              element={<News setProgress={this.setProgress} key="entertainment" pageSize={pageSize} country="in" category="entertainment" />}
            />
            <Route
              exact
              path="/health"
              element={<News setProgress={this.setProgress} key="health" pageSize={pageSize} country="in" category="health" />}
            />
            <Route
              exact
              path="/science"
              element={<News setProgress={this.setProgress} key="science" pageSize={pageSize} country="in" category="science" />}
            />
            <Route
              exact
              path="/sports"
              element={<News setProgress={this.setProgress} key="sports" pageSize={pageSize} country="in" category="sports" />}
            />
            <Route
              exact
              path="/technology"
              element={<News setProgress={this.setProgress} key="technology" pageSize={pageSize} country="in" category="technology" />}
            />
          </Routes>
        </Router>
      </div>
    );
}
