import React, { Component } from 'react'
import Newsitem from './Newsitem'

export class News extends Component {

    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false
        }
    }

     async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=06116f28526f48bcae830fb9eeff9cb2";
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({articles: parsedData.articles})
     }

handlePrevClick = async() => {
    console.log("Previous");

    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=06116f28526f48bcae830fb9eeff9cb2-${this.state.page-1}`;
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles: parsedData.articles})
    this.setState({
    page: this.state.page-1,
    articles: parsedData.articles
  })
}

   handleNextClick = async() => {
   console.log("Next");

   let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=06116f28526f48bcae830fb9eeff9cb2-${this.state.page+1}`;
   let data = await fetch(url);
   let parsedData = await data.json()
   console.log(parsedData);
   this.setState({articles: parsedData.articles})
   this.setState({
   page: this.state.page+1,
   articles: parsedData.articles
 })
}

    render() {
        return (
            <div className="container my-3">
                <h1>MayankNews - Top Headlines </h1>
                <div className="row">
                    {this.state.articles.map((element) => {
                       return <div className="col-md-4" key={element.url}>
                            <Newsitem title={element.title?element.title:""} description={element.description?element.description.slice:""} imageurl={element.urlToImage} newsurl={element.url}/>
                        </div>
                    })}

                </div>
                <div className="container d-flex justify-content-between">
                <button disabled={this.state.page<=1} class="btn btn-dark" onClick={this.handlePrevClick}> &larr;Previous</button>
                <button type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}

export default News
