import React, { Component } from 'react'
import Newsitem from './Newsitem'

export class News extends Component {

    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false
        }
    }

     async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=06116f28526f48bcae830fb9eeff9cb2";
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({articles: parsedData.articles})
     }



    render() {
        return (
            <div className="container my-3">
                <h1>MayankNews - Top Headlines </h1>
                <div className="row">
                    {this.state.articles.map((element) => {
                       return <div className="col-md-4" key={element.url}>
                            <Newsitem title={element.title?element.title:""} description={element.description?element.description.slice:""} imageurl={element.urlToImage} newsurl={element.url}/>
                        </div>
                    })}

                </div>
                <div className="container">
                <button type="button" class="btn btn-dark">Previous</button>
                <button type="button" class="btn btn-dark">Next</button>
                </div>
            </div>
        )
    }
}

export default News
