import React, { useEffect, useState } from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const updateNews = async () => {
    props.setProgress(10);
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=06116f28526f48bcae830fb9eeff9cb2&page=${page + 1}&pageSize=${props.pageSize}`;
    setLoading(true);
    let data = await fetch(url);
    props.setProgress(30);
    let parsedData = await data.json();
    if (parsedData) {
      setArticles(parsedData.articles || []);
      setTotalResults(parsedData.totalResults || 0);
      props.setProgress(70);
      props.setProgress(100);
    }
    // setArticles(parsedData.articles);
    // setTotalResults(parsedData.totalResults);
    // setLoading(false);
  };

  useEffect(() => {
    document.title = `${capitalizeFirstLetter(props.category)} - MayankNews`;
    updateNews();
    // eslint-disable-next-line
  }, [props.country, props.category, props.pageSize]);

  const handlePrevClick = () => {
    setPage(page - 1);
  };

  const handleNextClick = () => {
    setPage(page + 1);
  };

  const fetchMoreData = async () => {
    setPage(page + 1);
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=06116f28526f48bcae830fb9eeff9cb2&page=${page + 1}&pageSize=${props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    if (parsedData) {
      setArticles(parsedData.articles || []);
      setTotalResults(parsedData.totalResults || 0);
    }

  };

  return (
    <div className="container my-3">
      <h1 className="text-center" style={{ margin: '35px', marginTop: '90px' }}>
        MayankNews - Top {capitalizeFirstLetter(props.category)} Headlines
      </h1>
      {loading && <Spinner />}
      <InfiniteScroll
        dataLength={articles.length}
        next={fetchMoreData}
        hasMore={articles.length !== totalResults}
        loader={<Spinner />}
      >
        <div className="container">
          <div className="row">
            {articles.map((element) => (
              <div className="col-md-4" key={element.url}>
                <Newsitem
                  title={element?.title || ''}
                  description={element?.description || ''}
                  imageurl={element?.urlToImage}
                  newsurl={element?.url}
                  author={element?.author}
                  date={element?.publishedAt}
                  source={element?.source?.name}
                />

              </div>
            ))}
          </div>
        </div>
      </InfiniteScroll>
      <div className="container d-flex justify-content-between">
        <button
          className="btn btn-dark"
          onClick={handlePrevClick}
          disabled={page === 1}
        >
          &larr; Previous
        </button>
        <button
          className="btn btn-dark"
          onClick={handleNextClick}
          disabled={page * props.pageSize >= totalResults}
        >
          Next &rarr;
        </button>
      </div>
    </div>
  );
};

News.defaultProps = {
  country: 'in',
  pageSize: 8,
  category: 'general',
};

News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,
};

export default News;
